async function searchShows(){const e=document.getElementById("searchInput").value,t=(document.getElementById("resultsTable"),document.getElementById("results")),n=document.getElementById("loader");if(t.innerHTML="",""!==e.trim()){n.style.display="block",await new Promise((e=>setTimeout(e,2e3)));try{const a=await fetch(`https://api.tvmaze.com/search/shows?q=${e}`),o=await a.json();if(n.style.display="none",0===o.length)return void(t.innerHTML='<tr><td colspan="4">Nema rezultata za traženi pojam.</td></tr>');o.forEach((e=>{const n=document.createElement("tr"),a=document.createElement("td");a.textContent=e.show.name,n.appendChild(a);const o=document.createElement("td");o.textContent=e.show.genres.join(", "),n.appendChild(o);const s=document.createElement("td");s.textContent=e.show.summary,n.appendChild(s);const d=document.createElement("td");d.textContent=e.show.rating.average||"N/A",n.appendChild(d),t.appendChild(n)}))}catch(e){n.style.display="none",t.innerHTML='<tr><td colspan="4">Dogodila se greška, pokušajte kasnije.</td></tr>'}}else t.innerHTML='<tr><td colspan="4">Unesite traženi pojam.</td></tr>'}
